// Muestra contenido educativo y ejercicios según la lección seleccionada

import { obtenerAnatomiaWikipedia } from '../js/api.js';

// Obtener la materia desde la URL (?materia=Anatomía)
function getMateriaFromURL() {
    const params = new URLSearchParams(window.location.search);
    return params.get('materia') || 'Anatomía';
}

const materia = getMateriaFromURL();
const contenidoLeccion = document.getElementById('contenido-leccion');
const quizContainer = document.getElementById('quiz-container');

async function mostrarContenidoEducativo() {
    if (materia === 'Anatomía') {
        contenidoLeccion.innerHTML = '<p>Cargando contenido de anatomía...</p>';
        const contenido = await obtenerAnatomiaWikipedia();
        if (contenido.error) {
            contenidoLeccion.innerHTML = `<p>Error: ${contenido.error}</p>`;
            return;
        }
        contenidoLeccion.innerHTML = `
            <h2>${contenido.titulo}</h2>
            <p>${contenido.extracto}</p>
            ${contenido.imagen ? `<img src='${contenido.imagen}' alt='${contenido.titulo}' style='max-width:200px;'>` : ''}
            <p><a href='${contenido.url}' target='_blank'>Ver más en Wikipedia</a></p>
        `;
    } else {
        contenidoLeccion.innerHTML = `<h2>${materia}</h2><p>Contenido educativo próximamente.</p>`;
    }
}

